 const theme = Object.freeze({
  colors: {
    white: '#ffffff',
    black: '#010101',
    green: '#4caf50',
    red: '#f44336',
    blue: '#2196f3',
    shadowColor: 'rgb(153, 207, 255)',
    primaryText: 'rgb(7, 44, 51)',
    secondaryText: 'rgb(255, 255, 255)',
    backgroundColor: 'rgb(159, 225, 237)',
  },
  spacing: value => `${8 * value}px`,
});

export default theme;
