export const theme = Object.freeze({
  colors: {
    white: '#ffffff',
    black: '#010101',
    green: '#4caf50',
    red: '#f44336',
    blue: '#2196f3',
    primaryText: '#212121',
    secondaryText: '#757575',
    backgroundColor: 'rgb(159, 225, 237',
  },
  spacing: value => `${4 * value}px`,
});
