import styled from '@emotion/styled';

export const Table = styled.table`
  width: 1200px;
  text-transform: capitalize;
  text-align: center;
  margin: 0 auto;
  
 
  background-color: #9fe1ed;
  border-radius: 12px;
  box-shadow: 5px 5px 17px 0px #99CFFF;
  overflow: hidden;
  & td,
  & th {
    width: calc(100% / 3);
    padding: 20px;
  }
  & thead {
    color: white;
    text-transform: uppercase;
    background-color: #079ab5;
  }
  & tbody tr {
    background-color: #079ab5;
  }
  & tbody tr:nth-of-type(2n + 1) {
    background-color: #079ab5;
  }
`;
