import styled from "@emotion/styled";

export const Container = styled.div`
margin: 20px;
padding: 40px;
background-color: #9fe1ed;
text-align: center;
border-radius: 8px;
color: #01323b;`

export const Avatar = styled.img` 
width: 200px;
height: 200px;
background-color: #079ab5;
border-radius: 20px;
`

export const UserName = styled.p`   
 font-weight: 500;
 font-size: 20px;
 line-height: 1.17;
 text-align: center;
 padding-top: 30px;`

export const Stats = styled.ul`
 list-style: none;
 display: flex;
 border-bottom: 1px solid #072c33;`
