import styled from '@emotion/styled';

export const Table = styled.table`
  width: 1200px;
  text-transform: capitalize;
  text-align: center;
  margin-right: 100px;
  background-color: #9fe1ed;
  border-radius: 12px;
  box-shadow: 5px 5px 17px 0px #99CFFF;
  overflow: hidden;
  & td,
  & th {
    width: calc(100% / 3);
    padding: 20px; 
    /* border-right: 2px solid #072c33; */
  }
  & thead {
    color: white;
    text-transform: uppercase;
    background-color: #079ab5;
    font-weight: 500;
    font-size: 64px;
  }
  & tbody tr {
    background-color: ${props => props.theme.colors.backgroundColor};
  }

`;
